// Angular Rails Template
// source: app/assets/javascripts/templates/todo.html.erb

angular.module("templates").run(["$templateCache", function($templateCache) {
  $templateCache.put("todo.html", '<div class="container-fluid template-container">\n  <h1>todos</h1>\n\n  <div class="template-header">image placeholder</div>\n\n  <div id="todos-list" ng-controller="TodosController as todos">\n    <header>\n      <h1>{{ todos.todoList.length }} to dos</h1>\n      <h4>\n        {{ todos.completedTodos() }} completed tasks \n        <br>\n        {{ todos.remainingTodos() }} remaining tasks\n      </h4>\n    </header>\n\n    <main>        \n      <!-- begin add new todo -->\n      <section class="add-todo">\n        <input class="text-box" type="text" placeholder="Enter your task here" ng-model="todos.task">\n        <!-- below needs to be a calendar datepicker -->\n        <input class="text-box" type="text" placeholder="Due date" ng-model="todos.due">\n        <button class="btn btn-add" ng-click="todos.addTodo()">+</button>\n      </section>\n      <!-- end add new todo -->\n\n      <!-- begin show all todos -->\n      <section class="todo-list">\n        <ul>\n          <li ng-repeat="todo in todos.todoList">\n            <input class="checkbox" type="checkbox" ng-model="todo.done">\n            <span ng-class="{done: todo.done}">{{ todo.task }}</span>\n            <span ng-class="{done: todo.}">{{ todo.due }}</span>\n            <button class="btn btn-delete" ng-click="todos.deleteTodo($index)">x</button>\n          </li>\n        </ul>\n      </section>\n      <!-- end show all todos -->\n    </main>\n\n  </div>\n</div>')
}]);

